jQuery( document ).ready( function( b ) {
	var a = '#boldgrid_delete_forms, #boldgrid_delete_themes, #delete_pages';
	b( '#start_over' ).change( function( c ) {
		if ( ! c.currentTarget.checked ) {
			b( '#boldgrid-alert-remove' ).hide( 'slow' );
			b( a ).attr( 'disabled', 'disabled' );
			b( a ).attr( 'readonly', 'true' );
		} else {
			b( '#boldgrid-alert-remove' ).show( 'slow' );
			b( a ).removeAttr( 'disabled' );
			b( a ).removeAttr( 'readonly' );
		}
	} );
	b( '#start_over' ).trigger( 'change' );
} );
